&#34; #define lowbit(x) ((x)&amp;(-x)) typedef long long ll; const int N=1e5+7, mod=1e9+7; int n; int a[N]; void solve(){ cin&gt;&gt;n; for(int i=1;i&lt;=n;i++){ cin&gt;&gt;a[i]; } sort(a+1,a+1+n); if(n&gt;2){ cout&lt;&lt;&#34;No
&#34;; } else{ if(a[1]+1==a[2])cout&lt;&lt;&#34;No
&#34;; else cout&lt;&lt;&#34;Yes
&#34;; } } int main(){ ios::sync_with_stdio(0); cin.tie(0);cout.tie(0); int t=1; cin&gt;&gt;t; while(t--)solve(); return 0; } B. Game with Doors 相交片段的门一定都要关上，如果两端不重合就多关一扇 #include &lt;bits/stdc++.h&gt; using namespace std; #define ls now &lt;&lt; 1 #define rs now &lt;&lt; 1 | 1 #define endl &#34;
